import React, {useEffect, useState} from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
 

const  Movies = () => {
     

    const imgApi = ("https://image.tmdb.org/t/p/w1280/");
    const key = "0bf4238faa8f185063b116ee5de97fdb";
    const imgPath = 'https://image.tmdb.org/t/p/w1280/';
    const searchApi = "https://api.themoviedb.org/3/search/movie?sort_by=popularity.desc&api_key=fec79be029eaa0a5759cc99d46d4debb&query=";
    const videoApi = "https://www.themoviedb.org/movie/vidCode-black-widow#play=Fp9pNPdNwjI";

    const [data, setData] = useState([]);
    const [search, setSearch] = useState("");

    const getMoviesVid = async () => {
         
        const resp = await fetch("https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=fec79be029eaa0a5759cc99d46d4debb");
        const realData = await resp.json();
        console.log(realData.results);
        setData(realData.results);
    }
       
    useEffect(() => {
        getMoviesVid ();
         
    }, []); 
      
    const handleOnSubmit = (event) => {
        event.preventDefualt();

       fetch(searchApi+search)
       .then((resp) => resp.json())
       .then((data) => {
           setData(data.results);
       });
    }

    const handleOnChange = (event) => {
       setSearch(event.traget.value); 
    }
        
  return (
      <>
      {/* <h2>Roshan Thakur Is the world Websites</h2> */}
                
                <header>
                    <form action="" onSubmit={handleOnSubmit} >
                        <input type="search" 
                            placeholder="Search" 
                            className={search}
                            value="search"
                            onChange={handleOnChange}
                        />
                    </form>
                </header>
                
           
            
                {
                    data.map((curEle, ind)=> {
                        return (
                            <span key={ind}>
                         
                                <main >
                                    <div className="movie">
                                        <a href="https://www.themoviedb.org/movie/${curEle.id}-+${curEle.title}" method="_blank">
                                            <img 
                                                src={imgPath+curEle.poster_path}
                                                alt={curEle.title} />  
                                        </a> 
                                        <div className="movie_info">
                                            <h3> {curEle.title} </h3>
                                            <span class="${getclassByRate(vote_average)}"> {curEle.vote_average} </span>
                                        </div>
                                        <div className="overview">
                                            <h4>overview:</h4>
                                            <h5>Relese Date: {curEle.release_date}</h5>
                                            {curEle.overview} 
                                        </div>
                                    </div>
                                </main>
                            </span>
                          
                        )
                    })
                }
               
        </> 
    );
}

export default  Movies;
